import { useState } from "react";
import InputForm from './input';
import axios from "axios";

export default function Form() {
  // Initialize state variables
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [lastName, setLastName] = useState('');
  const [gender, setGender] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [address, setAddress] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [education, setEducation] = useState('');
  const [techSkills, setTechSkills] = useState('');

  const handleSubmit = event => {
    event.preventDefault();

    // Display form data in the console
    console.log(firstName, middleName, lastName, gender, dateOfBirth, address, phoneNumber, email, education, techSkills);

    // Create an object with form data
    const formData = {
      firstName: firstName,
      middleName: middleName,
      lastName: lastName,
      gender: gender,
      dateOfBirth: dateOfBirth,
      address: address,
      phoneNumber: phoneNumber,
      email: email,
      education: education,
      techSkills: techSkills
    };

    // Display form data object in the console
    console.log(formData);

    // Send form data to the server using axios
    axios.post("http://localhost:8000/form", formData);

    // Redirect to the table page
    window.location.href = '/table';
  };

  const handleReset = () => {
    // Reset all form fields to their initial values
    setFirstName('');
    setMiddleName('');
    setLastName('');
    setGender('');
    setDateOfBirth('');
    setAddress('');
    setPhoneNumber('');
    setEmail('');
    setEducation('');
    setTechSkills('');
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="first-name">First Name:</label>
          <InputForm
            type="text"
            id="first-name"
            value={firstName}
            onChange={e => setFirstName(e.target.value)}
          />
        </div>
        {/* Repeat the same pattern for other form fields */}
        {/* ... */}
        <button type="submit">Submit</button>
        <button type="reset" onClick={handleReset}>
          Reset
        </button>
      </form>
    </div>
  );
}
